mapper.select:
/* findOne :: 인터뷰 조회 */
SELECT *
FROM tb_interview
WHERE titleId = #{titleId}

mapper.insert:
/* save :: 인터뷰 주제 등록 */
INSERT INTO tb_interview
SET userId=#{userId}
, title=#{title}
, regDt=NOW()

mapper.update:
/* update :: 인터뷰 주제 수정 */
UPDATE tb_interview
SET title = #{title}
,updDt = Now()
WHERE titleId = #{titleId}

/* update :: 인터뷰 주제 삭제 */
UPDATE tb_interview
SET delYn = "Y"
WHERE titleId = #{titleId}
