mapper.select:
/* findOne :: 인터뷰 조회 */
SELECT *
FROM tb_interview
WHERE titleId = #{titleId}

mapper.insert:
/* save :: 인터뷰 주제 등록 */
INSERT INTO tb_interview
SET userId=#{userId}
, title=#{title}
, regDt=NOW()

mapper.update:
/* update :: 인터뷰 주제 수정 */
UPDATE tb_interview
SET title = #{title}
,updDt = DATE_FORMAT(NOW(),'%Y-%m-%d')
WHERE titleId = #{titleId} and userId = #{userId}

/* delete :: 인터뷰 주제 삭제 */
UPDATE tb_interview
SET delYn = 1
WHERE titleId = #{titleId}
